#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}

function getFCKZ101Q(){

	if(!validationChk()){
		return;
	}

	var orgcomps="";
	if(ComboBoxPlant.Value=="E1"){
		orgcomps ="PE";
	}
	else{
		orgcomps ="PS";
	}
	var sKind		= "getHOUSE";		
	var sMethodName = "service::pscomlib/selectMST040PF_HOUSE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF_HOUSE=ds_MST040PF_HOUSE";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " GCOMPS=" + quote(orgcomps); //회사(PE/PS)
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " MAJCD=" + quote("true"); //공장별 창고구분을 위해사용
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	//if (ds_MST040PF_HOUSE.rowcount > 0){
	var arrHouse = "('";
	for(i=0; i< ds_MST040PF_HOUSE.rowcount; i++){
		if(i == ds_MST040PF_HOUSE.rowcount-1){
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "')";
		}else{
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "','";
		}
	}

	/*
	if(ComboBoxPlant.Value == "E1"){
		arrHouse = "('1')";
	}else if(ComboBoxPlant.Value == "C1"){
		arrHouse = "('1')";
	}else if(ComboBoxPlant.Value == "S1"){
		arrHouse = "('2')";
	}else if(ComboBoxPlant.Value == "H1"){
		arrHouse = "('3')";
	}
	*/
	var pMajor="";
	if(chk_MAJOR.Value==true){
		pMajor="T";
	}
	var sKind		= "getFCKZ101Q";						
	var sMethodName = "service::psfaclib/selectFCKZ101Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FCKZ101Q=ds_FCKZ101Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@GRPCD=" + quote(cd_GRPCD.Value); //재고가시화 분류 (A:CKD)
		sArgument += "@MAJOR=" + quote(pMajor); //가시화 대상품목만
		sArgument += "@HOUSE=" + quote(arrHouse); //창고코드
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //erro r발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ComboBoxPlant.BKColor="white";

	if (ComboBoxPlant.Value == ""){
		ComboBoxPlant.BKColor="paleturquoise";
		alert(setPopMessage("2041"));//[알림] 사업부를 선택하세요.
		setMessage("2041");
		result = false;
	}
	return result;
}

