function setGBKcolor2(curSt, minSt, maxSt){
	var bkcolor="";
	if(parseFloat(curSt) < parseFloat(minSt)){
		bkColor="red";
	}else if(parseFloat(curSt) > parseFloat(maxSt)){
		bkColor="yellow";
	}else{ 
		bkColor="lime";
	}
	return bkColor;
}
function setMaker(curSt, minSt, maxSt){
	var maker="";
	if(parseFloat(curSt) < parseFloat(minSt)){
		maker="부족";
	}else if(parseFloat(curSt) > parseFloat(maxSt)){
		maker="과잉";
	}else{ 
		maker="정상";
	}
	return maker;
}
