function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		setMessage("1004");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
 function validationChk(){

	var result = true;
	st_WRKDT.BKColor="white";
	if (st_WRKDT.Value == "" )
	{
		st_WRKDT.BKColor="paleturquoise";
		alert(setPopMessage("2016"));//[알림] 날짜를 입력하세요.
		setMessage("2016");	
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "금형작업진척현황조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|금형작업진척현황조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

