//Focus선택한 것에 대한 grid bkcolor변경 //expr:setGBKcolor(GUBCD)
//이력구분에 따른 색상변경//expr:decode(GUBCD,"개발","lightcyan","양산설비","cornflower","수리","lightgoldenrodyellow","노후신작","lightcoral")
//선택된 ROW의 색변환 //BKSelColor
function setGBKcolor(gubVal){
	var bkcolor="";
	if(cmb_FOCUS.Value==gubVal){
		bkColor="lightcoral";
	}
	return bkColor;
}
function setGBKcolor2(wrkpln,wrksil){
	var bkcolor="";
	//alert(dalVal);
/*
	if(parseFloat(wrkpln) - parseFloat(wrksil)<1){
		bkColor="lime";
	}
	else if((parseFloat(wrkpln) - parseFloat(wrksil)) >=1 && (parseFloat(wrkpln) - parseFloat(wrksil))< 10){
		bkColor="yellow";
	}
	else if((parseFloat(wrkpln) - parseFloat(wrksil)) >=10){
		bkColor="red";
	}
	return bkColor;
*/
	if(parseFloat(wrkpln) - parseFloat(wrksil)<0.01){
		bkColor="lime";
	}
	else if((parseFloat(wrkpln) - parseFloat(wrksil)) >=0.01 && (parseFloat(wrkpln) - parseFloat(wrksil))< 0.3){
		bkColor="yellow";
	}
	else if((parseFloat(wrkpln) - parseFloat(wrksil)) >=0.3){
		bkColor="red";
	}
	return bkColor;
}
function cmb_INTERVAL_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (nNewIndex = 0) {
		//alert("10");
		setTimer(1, 10*1000);
	} else if (nNewIndex = 1) {
		//alert("20");
		setTimer(1, 20*1000);
	} else if (nNewIndex = 2) {
		//alert("30");
		setTimer(1, 30*1000);
	} else if (nNewIndex = 3) {
		//alert("40");
		setTimer(1, 40*1000);
	} else if (nNewIndex = 4) {
		//alert("50");
		setTimer(1, 50*1000);
	} else if (nNewIndex = 5) {
		//alert("60");
		setTimer(1, 60*1000);
	} else if (nNewIndex = 6) {
		//alert("stop");
		setTimer(1, 9999999999999999999999999999999);
	}		
}
function UserList_OnTimer(obj,nEventID)
{
	fn_Search();
}
