/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "전광판 월생산공정 이상발생현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|전광판 월생산공정 이상발생현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

