function fn_Search()
{
	getFGPZ050();
}

function fn_Input()
{
	if(checkInsertCount("ds_FGPZ050")== false) return;
	if(ds_FGPZ050.rowcount < 1) fn_Search();  
	ds_FGPZ050.AddRow();
	ds_FGPZ050.SetColumn(ds_FGPZ050.currow,"COMPS",ComboBoxPlant.Value);
	ds_FGPZ050.SetColumn(ds_FGPZ050.currow,"ROWGB", 0);
	ds_FGPZ050.SetColumn(ds_FGPZ050.currow,"SEQNO", 0);
	ds_FGPZ050.SetColumn(ds_FGPZ050.currow,"GIJUN", 0);
}

function fn_Delete()
{
	if(ds_FGPZ050.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FGPZ050.DeleteRow(ds_FGPZ050.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

