function cmb_ROWGB_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}

function ds_FGPZ050_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FGPZ050.GetRowType(ds_FGPZ050.currow)=="update" ||ds_FGPZ050.GetRowType(ds_FGPZ050.currow)=="insert" ){
		var p_PROCS=ds_FGPZ050.GetColumn(ds_FGPZ050.currow,"PROCS");
		var p_ROWGB=ds_FGPZ050.GetColumn(ds_FGPZ050.currow,"ROWGB");
		var p_SEQNO=ds_FGPZ050.GetColumn(ds_FGPZ050.currow,"SEQNO");
		var p_GIJUN=ds_FGPZ050.GetColumn(ds_FGPZ050.currow,"GIJUN");

		if(length(p_PROCS)==0){
			ds_FGPZ050.DeleteRow(ds_FGPZ050.currow);
		}
		return result;		
	}	
}

function ds_FGPZ050_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "ROWGB"){
		if(length(trim(varNewValue))==0){
			ds_FGPZ050.SetColumn(nRow,"ROWGB",0);
		}
	}
	if (strColumnID == "SEQNO"){
		if(length(trim(varNewValue))==0){
			ds_FGPZ050.SetColumn(nRow,"SEQNO",0);
		}
	}
	if (strColumnID == "GIJUN"){
		if(length(trim(varNewValue))==0){
			ds_FGPZ050.SetColumn(nRow,"GIJUN",0);
		}
	}
}

function cmb_FILTER_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cmb_FILTER.Value == ""){
		ds_FGPZ050.UnFilter();
	} else {
		ds_FGPZ050.Filter("ROWGB=='" + cmb_FILTER.Value + "'");
	}
}
//필터인경우 못찾음.
function ds_FGPZ050_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="PROCS"){ //중복체크
		if(varNewValue!=""){
			if(ds_FGPZ050_copy.FindRow("PROCS",varNewValue)!="-1"){
				alert(setPopMessage("2533"));
				ds_FGPZ050.SelectRow(ds_FGPZ050_copy.FindRow("PROCS",varNewValue));
				ds_FGPZ050.DeleteRow(nRow);
				return false;
			}
		}
	}
}
