var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1.4"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//cmb_WRKDT.Value="20141006";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false;
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFHDC207R(){

	if(!validationChk()){
		return;
	}
	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");

	var p_WRKDTF, p_WRKDTT;

	p_WRKDTF = substr(cmb_WRKDT.Value,0,6)+"01";
	p_WRKDTT = addDate(cmb_WRKDT.Value, -1);
	p_WRKDT = cmb_WRKDT.Value;

//alert(p_WRKDTF+"=="+ p_WRKDTT+"=="+ p_WRKDT);
	http.Sync 	= true;
	var sKind		= "getFHD035PF";		
	var sMethodName = "service::psfaclib/selectFHD035PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FHD035PF=ds_FHD035PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;

	var arrJYBDPT = "('";
	for(i=0; i< ds_FHD035PF.rowcount; i++){
		if(i == ds_FHD035PF.rowcount-1){
			arrJYBDPT = arrJYBDPT+ds_FHD035PF.GetColumn(i,"JYBDPT") + "')";
		}else{
			arrJYBDPT = arrJYBDPT+ds_FHD035PF.GetColumn(i,"JYBDPT") + "','";
		}
	}

	var sKind		= "getFHDC207R";						
	var sMethodName = "service::psfaclib/selectFHDC207R.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FHDC207R=ds_FHDC207R";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@WRKDTF=" + quote(p_WRKDTF); //근태일자FR
		sArgument += "@WRKDTT=" + quote(p_WRKDTT); //근태일자TO
		sArgument += "@WRKDT=" + quote(p_WRKDT); //근태일자
		sArgument += "@JYBDPT=" + quote(arrJYBDPT); //조회부서
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

	Ubi0.setJrfFileDir(G_baseURL+"ui/PSFACLIB/REPORT/");
	Ubi0.setJrfFileName("FHDC207R.jrf");		
	Ubi0.SetDataset("ds_FHDC207R", "ds_FHDC207R");
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);
	Ubi0.Retrieve();
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_WRKDT.BKColor="white";

	if (cmb_WRKDT.Value == ""){
		cmb_WRKDT.BKColor="paleturquoise";
		//alert("[알림]근태일자를 입력하세요.");
		alert(setPopMessage("6083"));//[알림]근태일자를 입력하세요.
		setMessage("6083");
		result = false;
	}
	return result;
}

