function fn_Input()
{

	if(checkInsertCount("ds_FHDZ060")== false) {
		return;
	}
	if(ds_FHDZ060.rowcount < 1) {
		fn_Search();
	}
	ds_FHDZ060.AddRow();
	ds_FHDZ060.SetColumn(ds_FHDZ060.currow,"COMPS",ComboBoxPlant.Value);
}

function fn_Delete()
{

	if(ds_FHDZ060.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FHDZ060.DeleteRow(ds_FHDZ060.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

