function ds_FHDZ060_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "MGMCD"){//일자
		if(varNewValue!=""){
			if(ds_FHDZ060.FindRow("MGMCD", varNewValue) >= 0){//조회된내용에서만 확인
				//alert("[경고] 이미 등록된 자료입니다.");  //[경고] 이미 등록된 자료입니다.
				alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				setMessage("1520");	
				return false;
			}
		}
	}
	if (strColumnID == "MGMNM"){//코드내용 글자수계산
		if(varNewValue!=""){
			var chkLeng = array(2);
				chkLeng = lengCal(varNewValue);
			if(chkLeng[1] == 18){
				//alert("[알림]입력가능한 글자수를 초과하였습니다."); //[알림]입력가능한 글자수를 초과하였습니다.
				alert(setPopMessage("6108")); //[알림]입력가능한 글자수를 초과하였습니다.
				setMessage("6108");	
				return false;
			}
		}
	}	
}

function ds_FHDZ060_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;

	if(ds_FHDZ060.GetRowType(ds_FHDZ060.currow)=="update" ||ds_FHDZ060.GetRowType(ds_FHDZ060.currow)=="insert" ){
		var p_MGMCD=ds_FHDZ060.GetColumn(ds_FHDZ060.currow,"MGMCD"); //코드
		var p_MGMNM=ds_FHDZ060.GetColumn(ds_FHDZ060.currow,"MGMNM"); //코드명

		if(length(p_MGMCD) == 0 && length(p_MGMNM) !=0 ){
				//alert("[알림]코드를 입력하세요.");
				alert(setPopMessage("6102")); //[알림]코드를 입력하세요.
				setMessage("6102");	
				return false;
		}
		if(length(p_MGMCD) !=0 && length(p_MGMNM) ==0 ){
				//alert("[알림]코드내용을 입력하세요.");
				alert(setPopMessage("6107")); //[알림]코드내용을 입력하세요.
				setMessage("6107");	
				return false;
		}
		if(length(p_MGMCD) ==0 && length(p_MGMNM) ==0 ){
			ds_FHDZ060.DeleteRow(ds_FHDZ060.currow);
		}
		return true;
	}
	http.usewaitcursor = true;
}

//글자길이계산 메소드
function lengCal(input)
{
	var availMsg = 0;//이전DATA TYPE
	var chkbyte = 0; //byte(길이계산
	var calj = 0; //실제length 계산이 한글과기타문자사이에는 길이 1+함,
	var msgVal = input;
	var rval = array(2);
	for(var i = 0 ; i < msgVal.length; i++){
		var ch =Asc(msgVal.CharAt(i));
		if(i==msgVal.length-1){//ex)'가'입력시 4byte
			if(i==0){//한글자입력시
				if( length(urlEncode(ch)) > 4){
					chkbyte +=4;//한글4
					calj+=2;
				}else{
					chkbyte++;//기타1
				}	
			}else{//이전에 값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1+1
						chkbyte+=2;
					}				
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj+=2;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}					
		}else{
			if(i==0){//처음에 값이 없는 경우
				if( length(urlEncode(ch)) > 4){
					chkbyte +=3;
					calj++;
				}else{
					chkbyte++;
				}	
			}else{//값이 있는경우
				if(length(availMsg)>4){//이전데이터가 한글
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte+=2;				
					}else{//기타1
						chkbyte+=2;				
						calj++;
					}	
				}else{
					if( length(urlEncode(ch)) > 4){//한글2
						chkbyte +=3;
						calj++;
					}else{//기타1
						chkbyte+=1;
					}					
				}
			}			
		}
		availMsg=urlEncode(ch);

		if(chkbyte>=18){
			rval[0]=i;
			//rval[1]=i+1+calj;
			rval[1]=chkbyte;
			return rval;
		}
	}
	return rval;
}
