function setExcel()
{
	var fName   = "특근신청서마감전/후";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|특근신청서마감전/후";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

