function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_CODE.Text);
	if (length(trim(ed_CODE.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "M6";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_CODE.Text = rtn[0];
		st_CODE.Text = rtn[1];
	}		
}
function getSYSDATE(){
	var sKind		= "getSYSDATE";						
	var sMethodName = "service::pscomlib/selectSYSDATE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SYSDATE=ds_SYSDATE";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;	
}


function ed_CODE_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		ds_FHDZ111R.ClearData();
		Ubi0.Retrieve();
		fn_Search();
	}	
}
