#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	cmb_TRNDT_F.Value = substr(nowDate,0,6)+"01";

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid_A.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid_A.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid_A.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid_A.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid_A.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid_A.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid_A.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid_A.UserData = MainGrid_A.Height;

		MainGrid_B.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid_B.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid_B.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid_B.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid_B.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid_B.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid_B.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid_B.UserData = MainGrid_B.Height;
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getFMZE201Q(){

	if(!validationChk()){
		return;
	}
	var str;
	if (cmb_ROLGB.Value == "A"){
		var sKind		= "getFMZE201Q_A";						
		var sMethodName = "service::psfaclib/selectFMZE201Q_A.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                           				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_FMZE201Q_A=ds_FMZE201Q_A";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += "@LIB1=" + quote(Lib1);
			sArgument += "@LIB2=" + quote(Lib2);
			sArgument += "@D30PR=" + quote("F#D30PR");
			sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += "@TRNDTF=" + quote(cmb_TRNDT_F.Value); //실적일자FR
			sArgument += "@TRNDTT=" + quote(cmb_TRNDT_T.Value); //실적일자TO
			sArgument += "@ROLGB=" + quote(cmb_ROLGB.Value); //내외구분	
		str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	}else if (cmb_ROLGB.Value == "B"){
		var sKind		= "getFMZE201Q_B";						
		var sMethodName = "service::psfaclib/selectFMZE201Q_B.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                           				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_FMZE201Q_B=ds_FMZE201Q_B";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += "@LIB1=" + quote(Lib1);
			sArgument += "@LIB2=" + quote(Lib2);
			sArgument += "@D30PR=" + quote("F#D30PR");
			sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += "@TRNDTF=" + quote(cmb_TRNDT_F.Value); //실적일자FR
			sArgument += "@TRNDTT=" + quote(cmb_TRNDT_T.Value); //실적일자TO
			sArgument += "@ROLGB=" + quote(cmb_ROLGB.Value); //내외구분	
		str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	}

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;

	if(cmb_TRNDT_F.Value == ""){
		alert(setPopMessage("6062"));//[알림]실적일자를 입력하세요.
		setMessage("6062");
		result = false;
	}else if(cmb_TRNDT_T.Value == ""){
		alert(setPopMessage("6062"));//[알림]실적일자를 입력하세요.
		setMessage("6062");
		result = false;
	}else if(cmb_TRNDT_F.Value > cmb_TRNDT_T.Value){
		alert(setPopMessage("6063"));//[알림]실적일자를 확인하세요.
		setMessage("6063");
		result = false;
	}
	return result;
}
/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FMZE201Q_A.ClearData();
	ds_FMZE201Q_B.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	if (cmb_ROLGB.Value == "A"){
		var fName   = "기간별 제품실적 재료비현황_제조원가";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid_A|기간별 제품실적 재료비현황_제조원가";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}else if (cmb_ROLGB.Value == "B"){
		var fName   = "기간별 제품실적 재료비현황_SC";
		var arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid_B|기간별 제품실적 재료비현황_SC";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
	}
}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

/********************************************************************** 
 * 내용설명 : 복사
 **********************************************************************/ 
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

