function cmb_ROLGB_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
function cmb_ROLGB_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "A"){
		MainGrid_A.Visible = true;
		MainGrid_B.Visible = false;
	}else if(strCode == "B"){
		MainGrid_B.top = 64;
		MainGrid_B.Left = 0;
		MainGrid_A.Visible = false;
		MainGrid_B.Visible = true;
	}
	ds_FMZE201Q_A.ClearData();
	ds_FMZE201Q_B.ClearData();
}
