 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setFPMC100(){
	//OR=|| AND=&&
/*	
	if(ds_FPMC100.GetRowType(ds_FPMC100.currow)=="update" || ds_FPMC100.GetRowType(ds_FPMC100.currow)=="insert" ){
		if(!ds_FPMC100_CanRowPosChange()){
			return;
		}
	}
*/
	var sKind		= "setSJTZ230";						
	var sMethodName = "service::psfaclib/saveFPMC100.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPMC100=ds_FPMC100:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPMC100_CHECK=ds_FPMC100_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_PLNYM.BKColor="white";

	if (length(st_PRDNM.Text)==0){
		ed_PRDCD.BKColor="paleturquoise";
		//alert("[알림]제품코드를 입력하세요.");//[알림]계획년월을 입력하세요.
		alert(setPopMessage("6045"));//[알림]제품코드를 입력하세요.
		setMessage("6045");
		result = false;
	}else if (cmb_PLNYM.Value == ""){
		cmb_PLNYM.BKColor="paleturquoise";
		//alert("[알림]계획년월을 입력하세요.");//[알림]계획년월을 입력하세요.
		alert(setPopMessage("6015"));//[알림]계획년월을 입력하세요.
		setMessage("6015");
		result = false;
	}
	return result;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPMC100_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPMC100_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPMC100_CHECK.GetColumn(i,"PSPNOS") + " = " + ds_FPMC100_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPMC100();  //error항목이 존재시에는 재조회
		return;
	}
	if(rtnCd<0){// tran 실패
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
