{
	var fName   = "월 기본생산계획수정(TX등록)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월 기본생산계획수정(TX등록)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

