function fn_Search()
{
	getFPMC100();
}

function fn_Input()
{
	if(checkInsertCount("ds_FPMC100")== false) return;
	if(ds_FPMC100.rowcount < 1) {
		if(!getFPMC100()){
			return;
		}
		if(ds_FPMC100.rowcount <1){
			if(!getSC003PR(substr(cmb_PLNYM.Value,0,6))){
				return;
			}
		}
	}
	ds_FPMC100.AddRow();
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"COMPSS",ComboBoxPlant.Value);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"PLNYMS",substr(cmb_PLNYM.Value,0,6));
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM0QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM1QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM2QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM3QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM4QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM5QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"MM6QYS",0);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"PRDCDS",ed_PRDCD.Text);
	ds_FPMC100.SetColumn(ds_FPMC100.currow,"PYSEQS",0);
}

function fn_Delete()
{
	if(ds_FPMC100.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FPMC100.DeleteRow(ds_FPMC100.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

