function ed_PRDCD_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync=true;
	var sKind		= "getMST050";		
	var sMethodName = "service::pscomlib/selectMST050PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument  += " GRPCD=" + quote("PRD");
		sArgument  += " MSTCD=" + quote(obj.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_MST050PF.GetRowCount() < 1){
		alert(setPopMessage("4023"));
		setMessage("4023");	
		obj.SetFocus();
		return;
	}
	http.usewaitcursor = true;
	st_PRDNM.Text=trim(ds_MST050PF.GetColumn(0,"MSTRM"));
}
//삭제 - 2019.09.25 최초등록일때만 칼렌다에 해당 년월일 등록되어 있는지 체크함. 
function cmb_PLNYM_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) return;

	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getSC003PR";		
	var sMethodName = "service::pscomlib/selectSC003PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC003PR=ds_SC003PR";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " PRMNO=" + quote("ABS"); //사업부
		sArgument += " YRMON=" + quote(substr(obj.Value,0,6)); //계획년월
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_SC003PR.GetRowCount() < 1){
		//alert("[알림]일자를 확인하세요.");
		alert(setPopMessage("6029"));//[알림]일자를 확인하세요.
		setMessage("6029");	
		cmb_PLNYM.SetFocus();
		return;
	}
	http.usewaitcursor = true;
}
function getITNBR(tval){

	var sKind		= "getITNBR";		
	var sMethodName = "service::pscomlib/selectBM008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM008PR=ds_BM008PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " ITNBR=" + quote(tval);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_BM008PR.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}
