function ds_FPMC100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PSPNOS"){
		if(varNewValue != "" && varOldValue != varNewValue){
/*		
			if(ds_FPMC100.FindRow("PSPNOS", trim(varNewValue)) >= 0){
				alert("[경고] 이미 등록된 자료입니다.");
				//alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				//setMessage("1520");
				ds_FPMC100.DeleteRow(nRow);
				ds_FPMC100.SelectRow(ds_FPMC100.FindRow("PSPNOS",varNewValue));					
				return false;
			}
*/			
			var tval=getITNBR(varNewValue);
			if(tval==0){
				alert("[경고] Item Master에 미등록된 품번입니다.");
				//alert(setPopMessage("1509")); //[경고] Item Master에 미등록된 품번입니다.
				//setMessage("1509");
				return false;
			}else{
				var tval=getEM001PR(varNewValue);
			}
		}
	}
}

function ds_FPMC100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PSPNOS"){
		if(varNewValue != "" && varOldValue != varNewValue){
			ds_FPMC100.SetColumn(nRow,"ITDSC",trim(ds_BM008PR.GetColumn(0,"ITDSC")));
			ds_FPMC100.SetColumn(nRow,"PLTCDS",trim(ds_BM008PR.GetColumn(0,"PACKC")));
			ds_FPMC100.SetColumn(nRow,"PFOTES",trim(ds_EM001PR.GetColumn(0,"REASN")));
			ds_FPMC100.SetColumn(nRow,"PCOSTS",trim(ds_EM001PR.GetColumn(0,"PRICE")));
		}else{
			ds_FPMC100.SetColumn(nRow,"ITDSC","");
			ds_FPMC100.SetColumn(nRow,"PLTCDS","");
			ds_FPMC100.SetColumn(nRow,"PFOTES","");
			ds_FPMC100.SetColumn(nRow,"PCOSTS","");
		}
	}
	if(strColumnID == "MM0QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM0QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);
	}
	if(strColumnID == "MM1QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM1QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);		
	}
	if(strColumnID == "MM2QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM2QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);
	}
	if(strColumnID == "MM3QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM3QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);
	}
	if(strColumnID == "MM4QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM4QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);
	}
	if(strColumnID == "MM5QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM5QYS",0);
		}
		var setMMQY=0;
		for(i=0; i<6; i++){
			setMMQY += parseInt(ds_FPMC100.GetColumn(nRow,"MM"+i+"QYS"));
		}
		ds_FPMC100.SetColumn(nRow,"SUMMMQY",setMMQY);
	}
	if(strColumnID == "MM6QYS"){
		if(varNewValue == ""){
			ds_FPMC100.SetColumn(nRow,"MM6QYS",0);
		}
	}
}
