#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var next_month;
var nowDate = today();
//FPME101PC로 날짜체크
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	cmb_PLMYM.Value=nowDate;
	cmb_TRNDT_F.Value=substr(nowDate,0,6)+"01";
	cmb_TRNDT_T.Value=nowDate;
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getCGMPARMHC(){
	var sKind		= "getCGMPARMHC";						
	var sMethodName = "service::pscomlib/selectCGMPARMHC.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMHC=ds_CGMPARMHC";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	next_month = addMonth(ds_CGMPARMHC.GetColumn(0,"ADSDT"),1);
}

function getFPME104Q(){

	if(!validationChk()){
		return;
	}

	getCGMPARMHC();
	//trace(next_month);
	var plnYM = substr(cmb_PLMYM.Value,0,6);
	if(plnYM > substr(next_month,0,6)){
		//alert("[알림]계획년월은 CIGMA년월+1까지 입력가능합니다.");
		alert(setPopMessage("6055"));  //[알림]계획년월은 CIGMA년월+1까지 입력가능합니다.
		setMessage("6055");	
		cmb_PYEAR.SetFocus();
		return;
	}
	var bpnslt="";
	var chgAdsdt="";
	if(ds_CGMPARMHC.rowcount<1){
		alert(setPopMessage("6058"));  //[알림]ORIGINAL DAY(CIGMA일자)가 존재하지않습니다.
		setMessage("6058");	
		return;
	}
	if(ds_CGMPARMHC.GetColumn(0,"SEQMON")==1){//당월이 M 인경우
		//bpnslt=1;
		chgAdsdt = addMonth(ds_CGMPARMHC.GetColumn(0,"ADSDT"),-1);
	}else{//당월이 M+1인경우
		//bpnslt=2;
		chgAdsdt = addMonth(ds_CGMPARMHC.GetColumn(0,"ADSDT"),-2);
	}

	var cigmaChk="";
	if(substr(cmb_PLMYM.Value,0,6) <= substr(chgAdsdt,0,6)){//원본!
		cigmaChk="1";
	}else{
		cigmaChk="2";
	}	
//alert(cigmaChk);
	//cigmaChk가 1인경우
	var mbCnt=0;
	var mcCnt=0;
	var mChk=0;
		mbCnt=parseInt(substr(cmb_PLMYM.Value,0,4))*12+parseInt(substr(cmb_PLMYM.Value,4,2));
		mcCnt=parseInt(substr(ds_CGMPARMHC.GetColumn(0,"ADSDT"),0,4))*12+parseInt(substr(ds_CGMPARMHC.GetColumn(0,"ADSDT"),4,2));
	if(ds_CGMPARMHC.GetColumn(0,"SEQMON")==1){//당월이 M 인경우
		mChk=mbCnt-mcCnt;
	}else{//당월이 M+1인경우
		mChk=(mbCnt-mcCnt)+1;
	}
//alert(mbCnt+"=="+mcCnt+"=="+mChk);
	var sKind		= "getFPME104Q";						
	var sMethodName = "service::psfaclib/selectFPME104Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPME104Q=ds_FPME104Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PLMYM=" + quote(substr(cmb_PLMYM.Value,0,6)); //계획년월
		sArgument += "@TRNDTF=" + quote(cmb_TRNDT_F.Value); //득의선FR
		sArgument += "@TRNDTT=" + quote(cmb_TRNDT_T.Value); //득의선TO
		sArgument += "@MCHK=" + quote(mChk); //m
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	for(i=0; i<4; i++){
		MainGrid.SetCellProp("Head",1+i, "text", substr(addMonth(cmb_PLMYM.Value,+i),0,4)+"-"+substr(addMonth(cmb_PLMYM.Value,+i),4,2) +"월");
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_PLMYM.BKColor="white";
	cmb_TRNDT_F.BKColor="white";
	cmb_TRNDT_T.BKColor="white";

	if (cmb_PLMYM.Value == ""){
		cmb_PLMYM.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}else if(cmb_TRNDT_F.Value == ""){
		cmb_TRNDT_F.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}else if(cmb_TRNDT_T.Value == ""){
		cmb_TRNDT_T.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}else if(cmb_TRNDT_F.Value > cmb_TRNDT_T.Value){
		cmb_TRNDT_F.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}
	return result;
}
