function setExcel()
{
	var fName   = "월생산계획대실적현황(차륜별)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월생산계획대실적현황(차륜별)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

