function setDalsu(ptrqyt, silqyt){
	var rVal=0;
	if(ptrqyt!=0){
		var chkVal=(silqyt/ptrqyt)*100;
		if(chkVal >999.9){
			rVal=99.99;
		}else{
			rVal=chkVal;
		}	
	}
	return rVal;
}
