var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var next_month;
//FPME101PC로 날짜체크
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getCGMPARMHC(){
	var sKind		= "getCGMPARMHC";						
	var sMethodName = "service::pscomlib/selectCGMPARMHC.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CGMPARMHC=ds_CGMPARMHC";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
}

function getFPME108Q(){

	if(!validationChk()){
		return;
	}
	var p_pyvnd1,p_pyvnd2;
	//p_pyvnd1 = iif (length(trim(ed_PYVND_F.Text)) == 0, '0000', trim(ed_PYVND_F.Text));
	p_pyvnd1 = iif (length(trim(ed_PYVND_F.Text)) == 0, '0', trim(ed_PYVND_F.Text));
	p_pyvnd2 = iif (length(trim(ed_PYVND_T.Text)) == 0 , '9999', trim(ed_PYVND_T.Text));

	var p_pspno2;
	p_pspno2 = iif(length(trim(ed_PSPNO_T.Text)) == 0 , '999999999999999', trim(ed_PSPNO_T.Text));

	getCGMPARMHC();

	var bpnslt="";
	var chgAdsdt="";
	if(ds_CGMPARMHC.rowcount<1){
		alert(setPopMessage("6058"));  //[알림]ORIGINAL DAY(CIGMA일자)가 존재하지않습니다.
		setMessage("6058");	
		return;
	}
	if(ds_CGMPARMHC.GetColumn(0,"SEQMON")==1){//당월이 M 인경우
		bpnslt=1;
		chgAdsdt = addMonth(ds_CGMPARMHC.GetColumn(0,"ADSDT"),-1);
	}else{//당월이 M+1인경우
		bpnslt=2;
		chgAdsdt = addMonth(ds_CGMPARMHC.GetColumn(0,"ADSDT"),-2);
	}

	var cigmaChk="";
	if(substr(cmb_PYEAR.Value,0,6) <= substr(chgAdsdt,0,6)){//원본!
		cigmaChk="1";
	}else{
		cigmaChk="2";
	}

	var mbCnt=0;
	var mcCnt=0;
	var mChk=0;
		mbCnt=parseInt(substr(cmb_PYEAR.Value,0,4))*12+parseInt(substr(cmb_PYEAR.Value,4,2));
		mcCnt=parseInt(substr(ds_CGMPARMHC.GetColumn(0,"ADSDT"),0,4))*12+parseInt(substr(ds_CGMPARMHC.GetColumn(0,"ADSDT"),4,2));
	if(ds_CGMPARMHC.GetColumn(0,"SEQMON")==1){//당월이 M 인경우
		mChk=mbCnt-mcCnt;
	}else{//당월이 M+1인경우
		mChk=(mbCnt-mcCnt)+1;
	}
	var sKind		= "getFPME108Q";						
	var sMethodName = "service::psfaclib/selectFPME108Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPME108Q=ds_FPME108Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PLMYM=" + quote(substr(cmb_PYEAR.Value,0,6)); //계획년월
		sArgument += "@PYEAR=" + quote(substr(cmb_PYEAR.Value,0,6)+"31"); //계획년월일
		sArgument += "@ROLGB=" + quote(cmb_ROLGB.Value); //내외구분
		sArgument += "@PYVNDF=" + quote(p_pyvnd1); //득의선FR
		sArgument += "@PYVNDT=" + quote(p_pyvnd2); //득의선TO
		sArgument += "@PSPNOF=" + quote(ed_PSPNO_F.Text); //공정그룹FR
		sArgument += "@PSPNOT=" + quote(p_pspno2); //공정그룹TO
		sArgument += "@MCHK=" + quote(mChk); //m
		sArgument += "@PGMCHK=" + quote(cigmaChk); //CIGMA계획 OR INTERFACE계획 CHECK
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	/*
	// TEST용
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PLMYM=" + quote(substr(cmb_PYEAR.Value,0,6)); //계획년월
		sArgument += " PYEAR=" + quote(substr(cmb_PYEAR.Value,0,6)+"31"); //계획년월일
		sArgument += " ROLGB=" + quote(cmb_ROLGB.Value); //내외구분
		sArgument += " PYVNDF=" + quote(p_pyvnd1); //득의선FR
		sArgument += " PYVNDT=" + quote(p_pyvnd2); //득의선TO
		sArgument += " PSPNOF=" + quote(ed_PSPNO_F.Text); //공정그룹FR
		sArgument += " PSPNOT=" + quote(p_pspno2); //공정그룹TO
		sArgument += " MCHK=" + quote(mChk); //m
		sArgument += " PGMCHK=" + quote(cigmaChk); //CIGMA계획 OR INTERFACE계획 CHECK
	http.TimeOut=1200;
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	http.TimeOut=30;
	*/
	for(i=0; i<7; i++){
		if(i=0){
			MainGrid.SetCellProp("Head",3+i, "text", substr(addMonth(cmb_PYEAR.Value,+i),0,4)+"-"+substr(addMonth(cmb_PYEAR.Value,+i),4,2) +" 생산계획");		
		}else{
			MainGrid.SetCellProp("Head",3+i, "text", substr(addMonth(cmb_PYEAR.Value,+i),0,4)+"-"+substr(addMonth(cmb_PYEAR.Value,+i),4,2) +" 생산계획");
		}
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_PLNYY.BKColor="white";

	if (cmb_PLNYY.Value == ""){
		cmb_PLNYY.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FPME108Q.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월생산계획금액집계표 득의선별(품번)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월생산계획금액집계표 득의선별(품번)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

/********************************************************************** 
 * 내용설명 : 복사
 **********************************************************************/ 
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

