function fn_print()
{
	if(confirm("선택된 제품작업지시TAG 인쇄합니까? ") == false) return;

	if(ds_FPQE100Q_4.rowcount <= 0 || ds_FPQE100Q_4.GetRowType(ds_FPQE100Q_4.row) != 'normal') return ;

	ds_FPQE100Q_5.ClearData();
	for (var row = 0 ; row < ds_FPQE100Q_4.RowCount ; row++ ){
		if(ds_FPQE100Q_4.GetSelect(row) == true){
			ds_FPQE100Q_5.addRow();
			ds_FPQE100Q_5.copyrow(ds_FPQE100Q_5.row,"ds_FPQE100Q_4",row);
		}
	}

	if(ds_FPQE100Q_5.rowcount <= 0) return ;

	var str = dialog("PSFACLIB::FPQE100QR.xml",
					" TABLE=" + quote(tableSelect) +
					" COMPS=" + quote(ComboBoxPlant.Value) +
					" PNYMD=" + quote(cal_YMD.Value) +
					" ds_SEL="+ quote(ds_FPQE100Q_5.SaveXML()),1100,600);		
}

