var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");


/*
ITTYP가 1또는 9인것만 입력가능함.(A-PART)
RPG는 모두 보여주지만 DIONE에서는 A-PART만 입력가능하믄로 A-PART만 보여줌.
FPW151PF =>QTEMP생성
*/
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.8"); //조회,삭제,저장, excel
	getComCode(); //공장구분 선택

	//ed_VNDNR.Text="A15";
	//cmb_PNYMD.Value=20140315;
 }

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}

function getFPWE161(){

	if(cmb_PNYMD.Value =""){
		//alert("[알림]계획일자를 확인하세요.");
		cmb_PNYMD.BKColor="paleturquoise";
		alert(setPopMessage("6081"));//[알림]계획일자를 확인하세요.
		setMessage("6081");
		cmb_PNYMD.SetFocus();
		return;		
	}


	http.usewaitcursor = false;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCDT=" + quote(cmb_PNYMD.Value); //계획일
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if (ds_MST090PF.GetRowCount() < 1){
		//alert("[알림]계획일자를 확인하세요.");
		alert(setPopMessage("6081"));//[알림]계획일자를 확인하세요.
		setMessage("6081");
		cmb_PNYMD.SetFocus();
		return;
	}
	http.usewaitcursor = true;


	var result = true;
	var sKind		= "getFPWE161";						
	var sMethodName = "service::psfaclib/selectFPWE161.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FPWE161=ds_FPWE161";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PLNYM=" + quote(cmb_PNYMD.Value); //계획일자
		sArgument += "@VNDNR=" + quote("J01"); //업체코드
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");
		result = false;
	}

}
/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setFPWE161(){
	//OR=|| AND=&&

	var sKind		= "setFPWE161";						
	var sMethodName = "service::psfaclib/saveFPWE161.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPWE161=ds_FPWE161:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWE161_CHECK=ds_FPWE161_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;	
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	cmb_PNYMD.BKColor="white";

	if (cmb_PNYMD.Value == ""){
		cmb_PNYMD.BKColor="paleturquoise";
		//alert("[알림]계획일자를 확인하세요.");
		alert(setPopMessage("6081"));//[알림]계획일자를 확인하세요.
		setMessage("6081");
		result = false;
	}
	return result;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPWE161_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPWE161_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPWE161_CHECK.GetColumn(i,"PRTNO") + " = " + ds_FPWE161_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPWE161();  //error항목이 존재시에는 재조회
		return;
	}
	if(rtnCd<0){// tran 실패
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FPWE161.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "일생산계획등록(Dcj)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|일생산계획등록(Dcj)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

/********************************************************************** 
 * 내용설명 : 복사
 **********************************************************************/ 
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

