function cmb_PNYMD_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(cmb_PNYMD.Value =""){
			//alert("[알림]계획일자를 확인하세요.");
			alert(setPopMessage("6081"));//[알림]계획일자를 확인하세요.
			setMessage("6081");
			cmb_PNYMD.SetFocus();
			return;		
		}
		fn_Search();
	}	
}

function ds_FPWE161_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "PLNQY"){
		if(varNewValue == ""){
			ds_FPWE161.SetColumn(nRow,"PLNQY",0);
		}
	}
}
