var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.3"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ed_PRTNO.Text="KE11616-00980PO";
	//cmb_PNYMD.Value="20150223";

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getFPWE900(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFPWE900";						
	var sMethodName = "service::psfaclib/selectFPWE900.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWE900=ds_FPWE900";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PNYMD=" + quote(cmb_PNYMD.Value); //지시일자
		sArgument += "@PRTNO=" + quote(ed_PRTNO.Text); //품번
		sArgument += "@ACTCD=" + quote(rdoggub.Value); //작업구분
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFPWE900(){

	var sKind		= "setFPWE900";						
	var sMethodName = "service::psfaclib/saveFPWE900.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPWE900=ds_FPWE900:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWE900_CHECK=ds_FPWE900_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPWE900_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPWE900_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPWE900_CHECK.GetColumn(i,"PRTNO") + "지시일자 : " +ds_FPWE900_CHECK.GetColumn(i,"PNYMD") + " = " + ds_FPWE900_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPWE900();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
		ed_PRTNO.BKColor="white";
		cmb_PNYMD.BKColor="white";

	if (length(trim(ed_PRTNO.Text))==0){
		ed_PRTNO.BKColor="paleturquoise";
		//alert("[알림] 품번을 입력하세요.");
		alert(setPopMessage("2017"));//[알림] 품번을 입력하세요.
		setMessage("2017");
		result = false;
	}else if(cmb_PNYMD.Value==""){
		cmb_PNYMD.BKColor="paleturquoise";
		//alert("[알림]지시일자를 입력하세요.");
		alert(setPopMessage("6076"));//[알림]지시일자를 입력하세요.
		setMessage("6076");
		result = false;
	}
	return result;
}

