 * 내용설명 : insert, update, delete
 **********************************************************************/ 
 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setFPWS161(){
	//OR=|| AND=&&

	var sKind		= "setFPWS161";						
	var sMethodName = "service::psfaclib/saveFPWS161.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPWS161=ds_FPWS161:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPWS161_CHECK=ds_FPWS161_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		//sArgument += " TABLE=" + quote(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"));//FPW151PF //삭제 : 2019.11.28 NEWDIONE로 SSO관리전환
		//sArgument += " TABLE=" + quote(trim(G_ds_user.GetColumn(0,"USER_ID")));//FPW151PF
		sArgument += " TABLE=" + quote("R"+trim(G_ds_user.GetColumn(0,"USER_ID")));//FPW151PF //2020.08.19 GCI-파라미터 변경으로 수정
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;	
}
/********************************************************************** 
 * 내용설명 : validation 체크
