function validationChk(){
	var result = true;
	cmb_PNYMD.BKColor="white";

	if (cmb_PNYMD.Value == ""){
		cmb_PNYMD.BKColor="paleturquoise";
		//alert("[알림]계획일자를 확인하세요.");
		alert(setPopMessage("6081"));//[알림]계획일자를 확인하세요.
		setMessage("6081");
		result = false;
	}
	return result;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPWS161_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPWS161_CHECK.GetRowCount();i++){
			alert("품번 : " +ds_FPWS161_CHECK.GetColumn(i,"PRTNO") + " = " + ds_FPWS161_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPWS161();  //error항목이 존재시에는 재조회
		return;
	}
	if(rtnCd<0){// tran 실패
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
