function getFPYC210Q(){

	if(!validationChk()){
		return;
	}

	ds_CNT.Clear();

	var sKind		= "getFPYC210Q_A";						
	var sMethodName = "service::psfaclib/selectFPYC210Q_A.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_FPYC210Q_A=ds_FPYC210Q_A";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PLNYY1=" + quote(substr(cmb_PLNYY.Value,0,4)+"0400"); //계획년
		sArgument += " PLNYY2=" + quote(parseInt(substr(cmb_PLNYY.Value,0,4))+1+"0300"); //계획년
		sArgument += " PLNGB=" + quote(cmb_PLNGB.Value); //계획구분
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	//Paging처리
	var sKind		= "getFPYC210Q_B_CNT";						
	var sMethodName = "service::psfaclib/selectFaclibCnt.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CNT=ds_CNT";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PLNYY1=" + quote(substr(cmb_PLNYY.Value,0,4)+"0400"); //계획년
		sArgument += " PLNYY2=" + quote(parseInt(substr(cmb_PLNYY.Value,0,4))+1+"0300"); //계획년
		sArgument += " PLNGB=" + quote(cmb_PLNGB.Value); //계획구분
		sArgument += " SQLID=" + "selectFPYC210Q_B_Cnt";
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	var dsCnt=0;
	var rowCnt=0;
		dsCnt =parseInt(ds_CNT.GetColumn(0,"CNT"));
	if(dsCnt>0){
		rowCnt=ceil(dsCnt/10000);
	}
	var pCnt_F=0;
	var pCnt_T=0;

	for(i=0; i<rowCnt; i++){
		if( i==0){
			pCnt_F =1;
			pCnt_T =10000; 
		}else{
			pCnt_F = (i*10000)+1; 
			pCnt_T = (i+1)*10000; 
		}

		var sKind		= "getFPYC210Q_B";						
		var sMethodName = "service::psfaclib/selectFPYC210Q_B.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_FPYC210Q_B_SUB=ds_FPYC210Q_B"; 		// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PLNYY1=" + quote(substr(cmb_PLNYY.Value,0,4)+"0400"); //계획년
			sArgument += " PLNYY2=" + quote(parseInt(substr(cmb_PLNYY.Value,0,4))+1+"0300"); //계획년
			sArgument += " PLNGB=" + quote(cmb_PLNGB.Value); //계획구분
			sArgument += " STARTNUM=" + quote(pCnt_F);
			sArgument += " ENDNUM=" +  quote(pCnt_T);			
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;

		//var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
		//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		if (str == 'T'){  //정상조회
				alert(setPopMessage("1004"));
			setMessage("1004");	
		}else if (str == 'B'){   //data값이 없을때
			alert(setPopMessage("1003"));
			setMessage("1003");	
			}else if (str == 'E'){   //error발생시
			alert(setPopMessage("1002"));
			setMessage("1002");	
		}

		ds_FPYC210Q_B.AppendDataset(ds_FPYC210Q_B_SUB);			
		ds_FPYC210Q_B_SUB.Clear();
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
		cmb_PLNYY.BKColor="white";

	if (cmb_PLNYY.Value == ""){
		cmb_PLNYY.BKColor="paleturquoise";
		alert(setPopMessage("6040"));//[알림]계획년을 입력하세요.
		setMessage("6040");
		result = false;
	}

	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FPYC210Q_A.ClearData();
	ds_FPYC210Q_B.ClearData();
	ds_FPYC210Q_B_SUB.ClearData();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "년 생산부하공수 현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid1|PROCESS별";
	arrSheets[1]  = "MainGrid2|ITEM별";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

/********************************************************************** 
 * 내용설명 : 복사
 **********************************************************************/ 
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

