var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ed_PYVND.Text="5700";
	//ed_PSPNO.Text="PE10311-01390TP";
	//cmb_PLNGB.Value="1";
	//cmb_PLNYY.Value="20130401";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getFPYE200(){

/*
	if(!validationChk()){
		return;
	}
*/
	ds_FPYE200D.ClearData();//defult datase clear
	var sKind		= "getFPYE200";						
	var sMethodName = "service::psfaclib/selectFPYE200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPYE200=ds_FPYE200";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PLNYY1=" + quote(substr(cmb_PLNYY.Value,0,4)+"04"); //계획년
		sArgument += "@PLNYY2=" + quote(parseInt(substr(cmb_PLNYY.Value,0,4))+1+"02"); //계획년
		sArgument += "@PLNYY3=" + quote(parseInt(substr(cmb_PLNYY.Value,0,4))+1+"03"); //계획년
		sArgument += "@PYVND=" + quote(ed_PYVND.Text); //득의선
		sArgument += "@SHPTO=" + quote(ed_SHPTO.Text); //SHPTO(미사용)
		sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //품번
		sArgument += "@PLNGB=" + quote(cmb_PLNGB.Value); //계획구분
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		ds_FPYE200D.AddRow();
		ds_FPYE200D.SetColumn(0,"EXDSP",trim(ds_FPYE200.GetColumn(0,"EXDSP")));
		ds_FPYE200D.SetColumn(0,"PROCS",trim(ds_FPYE200.GetColumn(0,"PROCS")));
		ds_FPYE200D.SetColumn(0,"YERQY",trim(ds_FPYE200.GetColumn(0,"YERQY")));
		ds_FPYE200D.SetColumn(0,"YERCS",trim(ds_FPYE200.GetColumn(0,"YERCS")));
		ds_FPYE200D.SetColumn(0,"CARCD",trim(ds_FPYE200.GetColumn(0,"CARCD")));
		//DEFUAL VALUES
		ds_FPYE200D.SetColumn(0,"PLTCD",trim(ds_FPYE200.GetColumn(0,"PLTCD")));
		ds_FPYE200D.SetColumn(0,"BDYCD",trim(ds_FPYE200.GetColumn(0,"BDYCD")));
		ds_FPYE200D.SetColumn(0,"PRDCD",trim(ds_FPYE200.GetColumn(0,"PRDCD")));
		ds_FPYE200D.SetColumn(0,"SPTEX",trim(ds_FPYE200.GetColumn(0,"SPTEX")));

		dummy_PLNDT();
		//기본 조회 후 수량/금액 계산하여 업데이트 처리시 사용
		displayDiv_ed_YERQY_OnKillFocus();
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/  
function setFPYE200(){

	if(!validationChk()){
		return;
	}

	if(displayDiv.ed_YERQY.Value ==0 && displayDiv.ed_YERCS.Value==0){
		alert("[알림]수량 및 단가를 확인하세요.");
		//alert(setPopMessage("6037"));//[알림]수량 및 단가를 확인하세요.
		///setMessage("6037");
		return;
	}else if(displayDiv.ed_YERQY.Value ==0 ){
		alert("[알림]수량는 0보다 커야합니다.");
		//alert(setPopMessage("6038"));//[알림]수량은 0보다 커야합니다.
		//setMessage("6038");
		return;
	}else if(displayDiv.ed_YERCS.Value ==0 ){
		alert("[알림]단가는 0보다 커야합니다.");
		//alert(setPopMessage("6039"));//[알림]단가는 0보다 커야합니다.
		//setMessage("6039");
		return;
	}else if(displayDiv.cmb_EXDSP.Value=""){
		alert("[알림] 내수구분을 입력하세요.");
		//alert(setPopMessage("4733"));//[알림]내수구분을 선택하세요.
		//setMessage("4733");
		return;
	//RGP에는 없으나 추가함.(생산계획입력시 품번에 공정코드는 들어가야함.)
	}else if(length(displayDiv.ed_PROCS.Text) ==0 ){
		alert("[알림] 공정코드를 입력하세요.");
		//alert(setPopMessage("2026"));//[알림] 공정코드를 입력하세요.
		//setMessage("2026");
		return;
	}
	//비율합계가 100 이되어야함.
	if(ds_FPYE200.sum("MRATE") > 100){
		alert(ds_FPYE200.sum("MRATE")+ "비율이 100이어야합니다.");
		//alert(setPopMessage("6044"));//[알림]비율은 100이하만 입력가능합니다.
		//setMessage("6044");
		return;
	}

	var sKind		= "setFPYE200";						
	var sMethodName = "service::psfaclib/saveFPYE200.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FPYE200=ds_FPYE200:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FPYE200_CHECK=ds_FPYE200_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FPYE200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FPYE200_CHECK.GetRowCount();i++){
			alert("계획년월 : " +ds_FPYE200_CHECK.GetColumn(i,"PLNYM") + " = " + ds_FPYE200_CHECK.GetColumn(i,"RTMSG"));
		}
		getFPYE200();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var pyvnd = st_PYVND.Text;
	var pspno = st_PSPNO.Text;	
		cmb_PLNYY.BKColor="white";
		ed_PYVND.BKColor="white";
		ed_PSPNO.BKColor="white";
		cmb_PLNGB.BKColor="white";

	if (cmb_PLNYY.Value == ""){
		cmb_PLNYY.BKColor="paleturquoise";
		alert("[알림]계획년월을 입력하세요.");
		//alert(setPopMessage("6040"));//[알림]계획년월을 입력하세요.
		//setMessage("6040");
		result = false;
	}else if(substr(cmb_PLNYY.Value,0,4) < 2003 ){
		cmb_PLNYY.BKColor="paleturquoise";
		alert("[알림]계획년은 2003보다 커야합니다.");
		//alert(setPopMessage("6041"));//[알림]계획년은 2003보다 커야합니다.
		//setMessage("6041");
		result = false;
	}else if(pyvnd == ""){
		ed_PYVND.BKColor="paleturquoise";
		alert("[알림]득의선을 입력하세요.");
		//alert(setPopMessage("6043"));//[알림]득의선을 입력하세요.
		//setMessage("6043");
		result = false;
	}else if(pspno == ""){
		ed_PSPNO.BKColor="paleturquoise";
		alert("[알림] 품번을 입력하세요.");
		//alert(setPopMessage("2017"));//[알림] 품번을 입력하세요.
		//setMessage("2017");
		result = false;
	}else if(length(cmb_PLNGB.Value) == 0){
		alert("[알림]계획구분을 선택하세요.");
		cmb_PLNGB.BKColor="paleturquoise";
		//alert(setPopMessage("6042"));//[알림]계획구분을 선택하세요.
		//setMessage("6042");
		result = false;
	}	
	return result;
}

