function fn_Input()
{
	if(ds_FPYE200.rowcount < 1) fn_Search();
	if(ds_FPYE200.GetRowCount() < 1){
		alert("[알림] 신규입력입니다.");
		//alert(setPopMessage("1013"));//[알림] 신규입력입니다.
		//setPopMessage("1013");
		//1.등록시 PART NO CROSS REFERANCE MASTER CHECK
		var sKind		= "getEM000PR";						
		var sMethodName = "service::pscomlib/selectEM000PR.do";    // 서버에서 호출 될 Method 명
		var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_EM000PR=ds_EM000PR";  	   // 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " CUSNO=" + quote(ed_PYVND.Text); //업체
			sArgument += " PRTNO=" + quote(ed_PSPNO.Text); //품번
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if(ds_EM000PR.rowcount<1){
			//alert("[확인] Part Number Cross Ref MA에 미 등록된 득의선,품번입니다.");
			alert(setPopMessage("4001"));//[확인] Part Number Cross Ref MA에 미 등록된 득의선,품번입니다.
			setMessage("4001");			
			return;
		}else{
			//2.등록시 ITEM MASTER CHECK
			var sKind		= "getBM008PR";		
			var sMethodName = "service::pscomlib/selectBM008PR.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_BM008PR=ds_BM008PR";
			var sArgument   = "";
				sArgument  += " LIB1=" + quote(Lib1);
				sArgument  += " ITNBR=" + quote(ed_PSPNO.Text);
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			if(ds_BM008PR.rowcount<1){
				//alert("[알림]ITEM MASTER A에 미등록된 품번입니다.);
				alert(setPopMessage("6032"));//[알림]ITEM MASTER A에 미등록된 품번입니다.
				setMessage("6032");			
				return;
			}else{
				//3.PROCESS CODE CHECK
				var sKind		= "getBM002PR";		
				var sMethodName = "service::psfaclib/selectBM002PR.do";    // 서버에서 호출 될 Method 명
				var sInDataSet  = "";
				var sOutDataSet = "ds_BM002PR=ds_BM002PR";
				var sArgument   = "";
					sArgument += " LIB1=" + quote(Lib1);
					sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
					sArgument += " PRTNO=" + quote(ed_PSPNO.Text); //품번
					sArgument += " FLAG=" + quote("PRTNO");
				http.Sync=true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync=false;
				if (ds_BM002PR.rowcount < 1){
					alert("[알림]Item & Process MA에 미등록된 품번입니다");
					//alert(setPopMessage("6035"));//[알림]Item & Process MA에 미등록된 품번입니다
					//setMessage("6035");	
					return;
				}else{
					//4.차종코드조회
					var sKind		= "getMST100PF";		
					var sMethodName = "service::pscomlib/selectMST100PF.do"; 
					var sInDataSet  = "";
					var sOutDataSet = "ds_MST100PF=ds_MST100PF";
					var sArgument   = "";
						sArgument  += " LIB2=" + quote(Lib2);
						sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
						sArgument  += " PRTNO=" + quote(ed_PSPNO.Text); //품번
				http.Sync=true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync=false;
					//5. 기본단가
					var sKind		= "getEM001PR";		
					var sMethodName = "service::pscomlib/selectEM001PR.do"; 
					var sInDataSet  = "";
					var sOutDataSet = "ds_EM001PR=ds_EM001PR";
					var sArgument   = "";
						sArgument  += " LIB1=" + quote(Lib1);
						sArgument  += " PRTNO=" + quote(ed_PSPNO.Text);
				http.Sync=true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync=false;
					//5. 특소세여부
					var sKind		= "getSMD010PF";		
					var sMethodName = "service::pscomlib/selectSMD010PF.do"; 
					var sInDataSet  = "";
					var sOutDataSet = "ds_SMD010PF=ds_SMD010PF";
					var sArgument   = "";
						sArgument  += " LIB2=" + quote(Lib2);
						sArgument  += " COMPS=" + quote(ComboBoxPlant.Value);
						sArgument  += " PYVND=" + quote(ed_PYVND.Text);
						sArgument  += " SHPTO=" + quote(ed_SHPTO.Text);
						sArgument  += " PSPNO=" + quote(ed_PSPNO.Text);
						sArgument  += " SPERT=" + quote("Y");//조회FLAG
				http.Sync=true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync=false;
					var spert = 0;
					var sptex = "";
					if(ds_SMD010PF.rowcount>0){
						spert = ds_SMD010PF.GetColumn(0,"SPERT");
					}
					if(spert >0){
						sptex ="Y";
					}
					ds_FPYE200D.AddRow();
					ds_FPYE200D.SetColumn(0,"EXDSP","");
					ds_FPYE200D.SetColumn(0,"PROCS",trim(ds_BM002PR.GetColumn(0,"PROCS")));
					ds_FPYE200D.SetColumn(0,"YERQY","");
					ds_FPYE200D.SetColumn(0,"YERCS",ds_EM001PR.GetColumn(0,"PRICE"));
					ds_FPYE200D.SetColumn(0,"CARCD",trim(ds_MST100PF.GetColumn(0,"CARCD")));
					//DEFUAL VALUES
					ds_FPYE200D.SetColumn(0,"PLTCD",trim(ds_BM008PR.GetColumn(0,"PACKC")));//공장코드
					ds_FPYE200D.SetColumn(0,"BDYCD",trim(ds_MST100PF.GetColumn(0,"BDYCD")));//BDY코드
					ds_FPYE200D.SetColumn(0,"PRDCD",trim(ds_MST100PF.GetColumn(0,"PRDCD")));//제품코드
					ds_FPYE200D.SetColumn(0,"SPTEX",sptex);//특소세여부

					for(i=0; i<12; i++){
						ds_FPYE200.AddRow();
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"COMPS",ComboBoxPlant.Value);
						if(i <= 8){
							ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLNYM",substr(cmb_PLNYY.Value,0,4)+lpad(i+4,0,2));	
						}else{
							ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLNYM",parseInt(substr(cmb_PLNYY.Value,0,4))+1+lpad(i-8,0,2));					
						}
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLNGB",cmb_PLNGB.Value);
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PYVND",ed_PYVND.Text);
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"SHPTO",0);
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PSPNO",ed_PSPNO.Text);
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLTCD",trim(ds_BM008PR.GetColumn(0,"PACKC")));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PRDCD",trim(ds_MST100PF.GetColumn(0,"PRDCD")));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"PROCS",trim(ds_BM002PR.GetColumn(0,"PROCS")));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"SPTEX",sptex);//특소세여부smd010
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"EXDSP","");//내수구분
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"CARCD",trim(ds_MST100PF.GetColumn(0,"CARCD")));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"BDYCD",trim(ds_MST100PF.GetColumn(0,"BDYCD")));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"YERQY","");
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"YERCS",ds_EM001PR.GetColumn(0,"PRICE"));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"MRATE",ds_MFT.GetColumn(i,"MFT"));
						ds_FPYE200.SetColumn(ds_FPYE200.currow,"MONQY","");				
					}
					dummy_PLNDT();
				}
			}
		}
	}else if(ds_FPYE200.GetRowCount() < 12){
		ds_FPYE200.AddRow();
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"COMPS",ComboBoxPlant.Value);
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLNGB",cmb_PLNGB.Value);
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PYVND",ed_PYVND.Text);
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"SHPTO",0);
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PSPNO",ed_PSPNO.Text);
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLTCD",ds_FPYE200D.GetColumn(0,"PLTCD"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PRDCD",ds_FPYE200D.GetColumn(0,"PRDCD"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"PROCS",ds_FPYE200D.GetColumn(0,"PROCS"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"SPTEX",ds_FPYE200D.GetColumn(0,"SPTEX"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"EXDSP",ds_FPYE200D.GetColumn(0,"EXDSP"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"CARCD",ds_FPYE200D.GetColumn(0,"CARCD"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"BDYCD",ds_FPYE200D.GetColumn(0,"BDYCD"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"YERQY",ds_FPYE200D.GetColumn(0,"YERQY"));
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"YERCS",ds_FPYE200D.GetColumn(0,"YERCS"));
		//ds_FPYE200.SetColumn(ds_FPYE200.currow,"MRATE""");
		ds_FPYE200.SetColumn(ds_FPYE200.currow,"MONQY","");
		for(i=0; i<ds_YMD.rowcount; i++){
			if(ds_FPYE200.FindRow("PLNYM",ds_YMD.GetColumn(i,"YMD"))=="-1"){
				ds_FPYE200.SetColumn(ds_FPYE200.currow,"PLNYM",ds_YMD.GetColumn(i,"YMD"));
				ds_FPYE200.SetColumn(ds_FPYE200.currow,"MRATE",ds_MFT.GetColumn(ds_MFT.FindRow("NO", substr(ds_YMD.GetColumn(i,"YMD"),4,2)),"MFT"));
				ds_FPYE200.Sort("PLNYM");
				return;
			}
		}
	}
}

