function displayDiv_cmb_EXDSP_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) {
		for(i=0; i<ds_FPYE200.rowcount; i++){
			ds_FPYE200.SetColumn(i,"EXDSP",obj.Text);
		}
		return;
	}
	if(obj.Text!="D" && obj.Text!="E" && obj.Text!="K"){
		alert(setPopMessage("6036"));//[알림]내수구분은 D,E,K만 입력가능합니다.
		setMessage("6036");	
		obj.SetFocus();
		return;
	}
	for(i=0; i<ds_FPYE200.rowcount; i++){
		ds_FPYE200.SetColumn(i,"EXDSP",obj.Text);
	}
}


function displayDiv_ed_PROCS_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) > 0) {
		var sKind		= "getBM002PR";		
		var sMethodName = "service::psfaclib/selectBM002PR.do";    // 서버에서 호출 될 Method 명
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM002PR=ds_BM002PR";
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " PRTNO=" + quote(ed_PSPNO.Text); //품번
			sArgument += " PROCS=" + quote(obj.Text); //품번
			sArgument += " FLAG=" + quote("PRTNOPROCS");
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;
			if (ds_BM002PR.rowcount < 1){
				alert("[알림]Item & Process MA에 미등록된 품번입니다");
				//alert(setPopMessage("6035"));//[알림]Item & Process MA에 미등록된 품번입니다
				//setMessage("6035");	
				obj.Text="";
			}
	}
	for(i=0; i<ds_FPYE200.rowcount; i++){
		ds_FPYE200.SetColumn(i,"PROCS",obj.Text);
	}	
}

function displayDiv_ed_YERQY_OnKillFocus(obj)
{
	if(length(trim(displayDiv.ed_YERQY.Value)) < 1) {
		displayDiv.ed_YERQY.Value=0;
	}
	var input=0;
	for(i=0; i<ds_FPYE200.rowcount; i++){
		ds_FPYE200.SetColumn(i,"YERQY",displayDiv.ed_YERQY.Value);
		if(i!=ds_FPYE200.rowcount-1){
			ds_FPYE200.SetColumn(i,"PQY",parseInt(ds_FPYE200.GetColumn(i,"YERQY")*ds_FPYE200.GetColumn(i,"MRATE")/100));
			ds_FPYE200.SetColumn(i,"PAM",parseInt(ds_FPYE200.GetColumn(i,"PQY")*ds_FPYE200.GetColumn(i,"YERCS")));
			input+=ds_FPYE200.GetColumn(i,"PQY");
		}else{
			ds_FPYE200.SetColumn(i,"PQY",parseInt(displayDiv.ed_YERQY.Value)-input);		
			ds_FPYE200.SetColumn(i,"PAM",parseInt((parseInt(displayDiv.ed_YERQY.Value)-input)*ds_FPYE200.GetColumn(i,"YERCS")));
		}
	}
}

function displayDiv_ed_YERCS_OnKillFocus(obj)
{
	if(length(trim(obj.Value)) < 1) {
		obj.Value=0;
	}
	for(i=0; i<ds_FPYE200.rowcount; i++){
		ds_FPYE200.SetColumn(i,"YERCS",obj.Value);
		ds_FPYE200.SetColumn(i,"PAM",parseInt(ds_FPYE200.GetColumn(i,"PQY")*ds_FPYE200.GetColumn(i,"YERCS")));
	}
}

//계획년월 dummy생성
function dummy_PLNDT(){
	for(i=0; i<12; i++){
		ds_YMD.AddRow();
		if(i <= 8){
			ds_YMD.SetColumn(i,"YMD",substr(cmb_PLNYY.Value,0,4)+lpad(i+4,0,2));	
		}else{
			ds_YMD.SetColumn(i,"YMD",parseInt(substr(cmb_PLNYY.Value,0,4))+1+lpad(i-8,0,2));					
		}
	}
}
function ds_FPYE200_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="MRATE"){
		if(varNewValue!=""){
			displayDiv_ed_YERQY_OnKillFocus();
		}else{
			ds_FPYE200.SetColumn(nRow, "MRATE", 0);
		}
	}
}
