function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	cal_PLNYM.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	//MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //30
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getFRME510Q(){
	if(length(trim(cal_PLNYM.Value)) < 8){
		alert(setPopMessage("2528"));  //[[확인] 마감년월을 확인바랍니다.
		setMessage("2528");	
		cal_PLNYM.SetFocus();
		return;
	}
	if(length(trim(cmb_CLSGU.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		cmb_CLSGU.SetFocus();
		return;
	}
	if(length(trim(ed_RATE.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		ed_RATE.SetFocus();
		return;
	}
	var p_pln1,p_pln2;
	p_pln1 = iif (length(trim(ed_PLANN1.Text)) == 0, '0', trim(ed_PLANN1.Text));
	p_pln2 = iif (length(trim(ed_PLANN2.Text)) == 0 , '99999', trim(ed_PLANN2.Text));
	//일자 SELECT(MENU)
	var sKind		= "getFRME510Q_DAY";						
	var sMethodName = "service::psfaclib/selectFRME510Q_DAY.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_FRME510Q_DAY=ds_FRME510Q_DAY";
	var sArgument   = "";
		sArgument   += " LIB2=" + quote(Lib2);	
		sArgument   += " PLNYM=" + quote(substr(cal_PLNYM.Value,0,6));
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var d1 = mid(ds_FRME510Q_DAY.GetColumn(0,"PLND1"),6,2)+"일";
	var d2 = mid(ds_FRME510Q_DAY.GetColumn(0,"PLND2"),6,2)+"일";
	var d3 = mid(ds_FRME510Q_DAY.GetColumn(0,"PLND3"),6,2)+"일";
	var d4 = mid(ds_FRME510Q_DAY.GetColumn(0,"PLND4"),6,2)+"일";
	var d5 = mid(ds_FRME510Q_DAY.GetColumn(0,"PLND5"),6,2)+"일";

	MainGrid.SetCellProp("Head", 8, "text",d1);
	MainGrid.SetCellProp("Head", 9, "text",d2);
	MainGrid.SetCellProp("Head", 10, "text",d3);
	MainGrid.SetCellProp("Head", 11, "text",d4);
	MainGrid.SetCellProp("Head", 12, "text",d5);

	//사내창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouse = "('";
	for (var i=0 ; toNumber(length(arrHouseT)) > i ; i++){
		if (i == length(arrHouseT)-1){
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "')";
		}else{
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "','";
		}
	}




	var sKind		= "getFRME510Q";						
	var sMethodName = "service::psfaclib/selectFRME510Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FRME510Q=ds_FRME510Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@CLSGU=" + quote(cmb_CLSGU.Value);
	sArgument += "@PLNYM=" + quote(substr(cal_PLNYM.Value,0,6));
	sArgument += "@HOUSE=" + quote(arrHouse);
	sArgument += "@PLANN1=" + quote(p_pln1);
	sArgument += "@PLANN2=" + quote(p_pln2);
	sArgument += "@RATE=" + quote(ed_RATE.Text);
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월품목분류별변경소요량";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월품목분류별변경소요량";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


