	this_month = substr(today(), 0, 6);
	//alert(substr(this_month, 4, 2));

	if (substr(this_month, 4, 2) = "03" or substr(this_month, 4, 2) = "04")
	{
		next_month = substr(addmonth(today(), 1), 0, 4) + "04";
