		MainGrid.BindDataset = "DS_FSLC201_11";
		MainGrid.CreateFormat();
		var fName   = "SIL22_" + substr(getDate(), 2, 10);
		var getLen=length(FileDialog0.FilePath);
		if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
			fName= "\\"+fName;
		}
		MainGrid.SaveExcelEx(FileDialog0.FilePath + fName + ".xls", "SIL22", "", false, "A1", false, false, false);			
	}	
	if (Checkbox11.Value = true and St_cnt12.Text <> '0')
	{
		MainGrid.BindDataset = "DS_FSLC201_12";
		MainGrid.CreateFormat();
		var fName   = "SIL23_" + substr(getDate(), 2, 10);
		var getLen=length(FileDialog0.FilePath);
		if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
			fName= "\\"+fName;
		}
		MainGrid.SaveExcelEx(FileDialog0.FilePath + fName + ".xls", "SIL23", "", false, "A1", false, false, false);			
	}	
}


