function getFSLE204(){

	var sKind		= "getFSLE204_1";						
	var sMethodName = "service::psfaclib/selectFSLE204_1.do";   // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "DS_FSLE204_1=DS_FSLE204_1";    			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";		
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	St_cnt1.Text = DS_FSLE204_1.GetRowCount();
}

/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{	/*
	var fName   = trim(left(ComboSQL.Text, 6));
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|" + fName;
	createExcelSheetS2(FileDialog0, fName + "_" +substr(getDate(), 2, 10), arrSheets);	
	*/
	if (Checkbox0.Value = false and Checkbox1.Value = false and Checkbox2.Value = false and Checkbox3.Value = false and Checkbox4.Value = false and Checkbox5.Value = false and Checkbox6.Value = false and Checkbox7.Value = false and Checkbox8.Value = false and Checkbox9.Value = false and Checkbox10.Value = false and Checkbox11.Value = false)
	{
		alert("하나 이상 선택하세요!");
		return;
	}

	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}

	if (Checkbox0.Value = true and St_cnt1.Text <> '0')
	{
		MainGrid.BindDataset = "DS_FSLE204_1";
		MainGrid.CreateFormat();		
		var fName   = "SIL21_" + substr(getDate(), 2, 10);	
		var getLen=length(FileDialog0.FilePath);
		if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
			fName= "\\"+fName;
		}
		MainGrid.SaveExcelEx(FileDialog0.FilePath + fName + ".xls", "SIL21", "", false, "A1", false, false, false);		
	}		
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBIDZ501"){
		// 조회건수가 없을경우
		if(ds_BIDZ501.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function on_cbSearchCompanyChange(obj,strCode,strText,nOldIndex,nNewIndex)	
{
	if( cb_searchCompany.index != 0 ){
		ds_VComCode_PLNT.Filter("CODE1=='" + cb_searchCompany.Value + "'");
		cb_searchCodeName.index = 0;
	}else{
		ds_VComCode_PLNT.UnFilter();
	}
}

