function detail_View(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_FYJE100CPD.rowcount <= 0 || ds_FYJE100CPD.GetRowType(ds_FYJE100CPD.row) != 'normal') return ;
	var str = dialog("PSFACLIB::FYJE100CPDD.xml","PP_COMPS=" + ed_comps.Value + " " + "PP_PYVND=" + ed_cusno.Value + " " + "PP_PYVNM=" + ed_cusnm.Value+ " " + "PP_CARCD=" + ds_FYJE100CPD.GetColumn(ds_FYJE100CPD.row,"CARCD"),1100,600);
}

function Button0_OnClick(obj)
{
	detail_View(obj);
}

