	ed_PSPNO.Text="";	
}

function rdoggub_OnClick(obj)
{
	if(rdoggub.Value==2){
		ed_PROCS.Enable=false;
	}else{
		ed_PROCS.Enable=true;
	}
}

function ed_PSPNO_OnCharChanged(obj,strPreText,strPostText)
