function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	Div1.cb_searchCodeName1.Value = comps;

}

/********************************************************************** 
 * 내용설명 : Excel FILE 가져오기
 * 주요변수 : 
 **********************************************************************/ 
function Div1_Button0_OnClick(obj)
{

	if(length(trim(Div1.ed_VNDNR.Text)) != 3){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_VNDNR.SetFocus();
		return;
	}
	if(length(trim(Div1.st_VNAME.Text)) < 1){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_VNDNR.SetFocus();
		return;
	}
	FileDialog0.Filter = "ALL File(.xls)|*.xls";	
	FileDialog0.Type = "OPEN";
	if (!FileDialog0.Open()) {
		alert(setPopMessage("1009"));	//  파일 선택 창을 여는데 실패하였습니다.
		return;
	}

	ds_SEND.ClearData();
	ds_ERCHK.ClearData();
	ds_MST090PF.ClearData();
	ds_BM008PR.ClearData();

	Div2.Visible = true;
	var file_name = FileDialog0.FileName;
	var file_path = FileDialog0.FilePath;
	var path = file_path + "\\" + file_name;
	setMessageDirect(path);	
	Div1.ed_path.Text = path;
	ext_ExcelImportByIndex(path,0,"ds_SEND",1,1,0,2);  

	if (ds_SEND.GetRowCount() < 1){
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	ds_SEND.Sort("PSPNO");

	//excel data 유효성 검사 및 전표마스타dataset생성 
	chkExcelData();

	Div2.Visible = false;

	if (ds_ERCHK.rowcount>0){
		alert(ds_ERCHK.rowcount+"건의 오류가 발생하였습니다. Excel수정후 다시 작업바랍니다.");
	}
}
/********************************************************************** 
 * 내용설명 : AS포장단가생성 
 * 주요변수 : 
 **********************************************************************/ 
 function setFZME120UC(){
	if (ds_SEND.GetRowCount() < 1) {
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	if(length(trim(Div1.ed_VNDNR.Text)) != 3){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_VNDNR.SetFocus();
		return;
	}
	if(length(trim(Div1.st_VNAME.Text)) < 1){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_VNDNR.SetFocus();
		return;
	}
	if (ds_ERCHK.rowcount > 0){
		alert("[경고] error항목 존재. 생성가능여부를 확인하시고 다시 작업바랍니다."); 
		return;
	}

	if(confirm(Div1.ed_VNDNR.Text + "  " + Div1.st_VNAME.Text +"업체 생성하시겠습니까? ") == false) return;

 	http.Sync = true;
	var sKind		= "SAVE_FZME120UC";						
	var sMethodName = "service::psfaclib/saveFZME120UC.do";
	var sInDataSet  = "ds_SEND=ds_SEND";
	var sOutDataSet = "ds_FZME120UC_CHECK=ds_FZME120UC_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " VNDNR=" + quote(Div1.ed_VNDNR.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "SAVE_FZME120UC"){
		if (ds_FZME120UC_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_FZME120UC_CHECK.GetRowCount();i++){
				var errMessage = "'" + ds_FZME120UC_CHECK.GetColumn(i,"PSPNO") + "'";
				errMessage +=" 는 이미 등록된 품번으로 생성되지 않았습니다.";
				alert(errMessage);
			}	
			return;	
		}		
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
		Div1.ed_VNDNR.Text = "";
		Div1.st_VNAME.Text =" ";
		ds_SEND.ClearData();
		ds_ERCHK.ClearData();
	}
}

function chkExcelData(){
	var rVNDNR = Div1.ed_VNDNR.Text;
	var rPSPNO; 
	var rIDATE;
	var rIFOTE;
	var rICOST; 
	var rIMARK;
	for(var z=0;z<ds_SEND.GetRowCount();z++){
		rPSPNO = trim(ds_SEND.GetColumn(z,"PSPNO"));
		rIDATE = ds_SEND.GetColumn(z,"IDATE");
		rIFOTE = ds_SEND.GetColumn(z,"IFOTE");
		rICOST = toNumber(ds_SEND.GetColumn(z,"ICOST"));
		rIMARK = ds_SEND.GetColumn(z,"IMARK");

		//length check
		if (length(rPSPNO) < 1 || length(rPSPNO) > 15 || length(rIDATE) != 8 || length(rIFOTE) != 1 || length(trim(rICOST)) < 1 || length(trim(rIMARK)) > 18 ) {
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",0);
			ds_ERCHK.SetColumn(rRow,"ERRNM","Length Error");
		}

		//숫자check
		if (!numberChk(rIDATE) || !numberChk(rICOST)){ 
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",0);
			ds_ERCHK.SetColumn(rRow,"ERRNM","number Error");
		}

		//품번 미등록 Check
		http.Sync 	= true;
		var sKind		= "getPrtno";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(trim(rPSPNO));
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BM008PR.GetRowCount() < 1){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",1);
			ds_ERCHK.SetColumn(rRow,"ERRNM","품번 Error");
		}
		// EXCEL DATA 중복check
		if( z > 0){
			if ( ds_SEND.FindRow("PSPNO",rPSPNO,z+1,ds_SEND.GetRowCount()) != -1 ){
				rRow=ds_ERCHK.InsertRow();
				ds_ERCHK.SetColumn(rRow,"X",z+1);
				ds_ERCHK.SetColumn(rRow,"Y",1);
				ds_ERCHK.SetColumn(rRow,"ERRNM","EXCEL에 중복품번 존재 Error");
			}
		}
		//
		http.Sync 	= true;
		var sKind		= "getFZM120PF";		
		var sMethodName = "service::psfaclib/selectFZM120PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_FZM120PF=ds_FZM120PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " PSPNO=" + quote(trim(rPSPNO));
			sArgument  += " VNDNR=" + quote(trim(rVNDNR));
			sArgument  += " IDATE=" + quote(trim(rIDATE));
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_FZM120PF.GetRowCount() > 0){
			if (toNumber(ds_FZM120PF.GetColumn(0,"CNT")) > 0){
				rRow=ds_ERCHK.InsertRow();
				ds_ERCHK.SetColumn(rRow,"X",z+1);
				ds_ERCHK.SetColumn(rRow,"Y",1);
				ds_ERCHK.SetColumn(rRow,"ERRNM","적용일자가 동일한 등록된 품번 Error");
			}
		}

		//적용일자 Check
		http.Sync 	= true;
		var sKind		= "getMST090PF";		
		var sMethodName = "service::pscomlib/selectMST090PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST090PF=ds_MST090PF";
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(Div1.cb_searchCodeName1.Value); //사업부
			sArgument += " PRCDT=" + quote(rIDATE); //전표일자
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_MST090PF.GetRowCount() < 1){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",2);
			ds_ERCHK.SetColumn(rRow,"ERRNM","적용일자 Error");
		}

		//단가구분 Check
		if (!(rIFOTE == "T" || rIFOTE == "F")){ 
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",3);
			ds_ERCHK.SetColumn(rRow,"ERRNM","단가구분 Error");
		}

		//단가 Check
		if (rICOST == 0 || rICOST > 9999999999){ 
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",4);
			ds_ERCHK.SetColumn(rRow,"ERRNM","단가 Error");
		}
		var pChk = toNumber(numberPOTChk(trim(rICOST))); // 소숫점 자리수 반환
		if (pChk > 4){ 
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",4);
			ds_ERCHK.SetColumn(rRow,"ERRNM","단가소숫점 Error");
		}

		//참조 Check
		var newVal = chkbytelen_db2(rIMARK, 20); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)!=0){ 
			ds_SEND.SetColumn(z,"IMARK",newVal);
		}
	}
}
/********************************************************************** 
 * 내용설명 : 시간형식Check
 **********************************************************************/ 
function Time_Check(strText)
{
	var strChk_t = replace(strText,':','');
	var strChk = right("0000"+strChk_t,4);
	if((left(strChk,2) > 24) || (right(strChk,2) > 59)){
		return false;
	}
	return true;
}

