function Div1_ed_VNDNR_OnKillFocus(obj)
{
	if (length(trim(obj.Text)) < 1){
		return;
	}
	var sKind		= "getVENNAM";		
	var sMethodName = "service::pscomlib/selectVENNAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_VENNAM=ds_VENNAM";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " VNDNR=" + quote(trim(obj.Text));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_VENNAM.GetRowCount() != 1){
		alert(setPopMessage("4674"));
		setMessage("4674");	
		st_VNAME.Text = "";
		Div1.ed_VNDNR.SetFocus();
		return;
	}
	Div1.st_VNAME.Text = ds_VENNAM.GetColumn(0,"VNAME");	
}

