/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
	getComCode();
	MainGrid.UserData = MainGrid.Height;
}

/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Top = G_ds_design.GetColumn(0,"SBTop");

	//displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getInOutTime(){

	if(ComboBoxPlant.Value !="C1" && ComboBoxPlant.Value !="S1"){
		alert("[알림]사용가능한 사업부가 아닙니다.");
		return;
	}

	var sKind		= "getInOutTime02";						
	var sMethodName = "service::pshumlib/selectGATE02.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_GATE02=ds_GATE02"; 
	var sArgument   = "";
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);	
		sArgument += "@ACKTIME=" + quote(cmb_KNYMD.Text);
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setText()
{
	var fName = "Gate근태_"+ComboBoxPlant.Text+"_"+cmb_KNYMD.Value;
	var buf="";
	for(i=0; i<ds_GATE02.rowcount; i++){
	/* 2019.04.30 사번추가
		buf += ds_GATE02.GetColumn(i,"regdt") +"	";
		buf += ds_GATE02.GetColumn(i,"regtm") +"	";
		buf += ds_GATE02.GetColumn(i,"cardno") +"	";
		buf += ds_GATE02.GetColumn(i,"attendcate");
	*/	

		buf += ds_GATE02.GetColumn(i,"regdt") +"	";
		buf += ds_GATE02.GetColumn(i,"regtm") +"	";
		buf += ds_GATE02.GetColumn(i,"cardno") +"	";
		buf += ds_GATE02.GetColumn(i,"attendcate") +"	";
		buf += ds_GATE02.GetColumn(i,"emp_no");
		if(i != (parseInt(ds_GATE02.rowcount)-1)){
			buf += "\r\n";
		}
	}
	//trace(buf);

	FileDialog0.FileName= fName; //파일명

	if(FileDialog0.open()){
		File.FileName=FileDialog0.FilePath+"\\"+FileDialog0.FileName+".txt";
		File.Open ("w+");
		var len = File.Write(buf, -1);
		//alert ( "파일에 쓴 Buffer의 Size = " + len );
		File.Close();
	}
}

