      TaxNm = "마산세무서장";
   }
   else if (ComboBoxPlant.Value == 'H1'){
      ssid = "310-85-09579";
      CName = "덴소코리아오토모티브주식회사";
      tName = "김경섭";
      TsId = "110111-2040585";
	  Addr = "충남 홍성군 은하면 구성남로 386";
      TaxNm = "홍성세무서장";
   }
   else if (ComboBoxPlant.Value == 'S1'){
      ssid = "124-85-61218";
      CName = "덴소코리아오토모티브주식회사";
      tName = "김경섭";
      TsId = "110111-2040585";
      JName = "덴소코리아오토모티브(주)";
      Addr = "경기도 화성시 장안면 장안공단5길 9";
      TaxNm = "화성세무서장";
   }

	if(chkEND.Value == '1'){
		if(rdoggub.Value=="Y"){
			if(cmb_PTJYM.Value == ''){
				cmb_PTJYM.BKColor="paleturquoise";
				alert("연말정산 마감년도를 입력하세요.");
				return;
			}
		}else{
			if(cmd_PTJYM_FR.Value == ""){
				cmd_PTJYM_FR.BKColor="paleturquoise";
				alert("중도정산 마감년월을 입력하세요.");
				return;
			}else if(cmd_PTJYM_TO.Value == ""){
				cmd_PTJYM_TO.BKColor="paleturquoise";
				alert("중도정산 마감년월을 입력하세요.");
				return;
			}else if(cmd_PTJYM_FR.Value > cmd_PTJYM_TO.Value){
				cmd_PTJYM_FR.BKColor="paleturquoise";
				alert("중도정산 마감년월을 확인하세요.");
				return;
			}
		}
	}
   	http.Sync = true;
	var sKind		= "getHAYZ082R_1";						
	var sMethodName = "service::pshumlib/selectHAYZ082R_1.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
