	var sOutDataSet = "ds_HAYZ082R_1=ds_HAYZ082R_1";   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	if(CmbGubun.Value == '2'){
		if (ed_SABUN.Text != ''){
			sArgument += "@SABUN=" + quote(ed_SABUN.Text);
		}else{
			alert("사번을 입력하여 주십시오.");
			return;
		}
	} else {
		if (cmdFORCD.Value != ""){
			sArgument += "@FORCD=" + quote(cmdFORCD.Value);
		}

		if (edtDPTCD1.Value != "" && edtDPTCD2.Value != "") {
			sArgument += "@DPTCD1=" + quote(edtDPTCD1.Value); 
			sArgument += "@DPTCD2=" + quote(edtDPTCD2.Value); 		
		}
	}

	if (cmb_GUNMU.Value != '0'){
		sArgument += "@PAYCD=" + quote(cmb_GUNMU.Value);
	}

	sArgument += "@PTJYM=" + quote(LEFT((cmb_PTJYM.Value),4)+13);//연말정산
	sArgument += "@PTJYM_FR=" + quote(substr(cmd_PTJYM_FR.Value,0,6));//중도정산FR
	sArgument += "@PTJYM_TO=" + quote(substr(cmd_PTJYM_TO.Value,0,6));//중도정산TO		
	sArgument += "@USER_ID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));		
	sArgument += "@MAGAM=" + quote(chkEND.Value);		
	sArgument += "@GUBCD=" + quote(rdoggub.Value);//구분코드(중도:J, 연말:Y)
	sArgument += "@SELGUB=" + quote("1");//1:경리팀, 2:사용자
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

//trace(ds_HAYZ082R_1.rowcount+"=="+ds_HAYZ082R_2.rowcount);
	MainGrid.setJrfFileDir(G_baseURL+"ui/PSHUMLIB/REPORT/");

	if(ds_HAYZ082R_1.GetColumn(0, "PTJYM") > 201401){
		//trace("2014");
		//MainGrid.setJrfFileName("HAYZ082R_2013.jrf");
		MainGrid.setJrfFileName("HAYZ082R_"+substr(ds_HAYZ082R_1.GetColumn(0, "PTJYM"),0,4)+".jrf");
	}else {
		//trace("2013");
		MainGrid.setJrfFileName("HAYZ082R_2013.jrf");
	}
	//MainGrid.setJrfFileName("HAYZ082R_2013.jrf");
	MainGrid.SetDataset("ds_HAYZ082R_1", "ds_HAYZ082R_1");
	MainGrid.SetArgument("COMPS", ComboBoxPlant.Value);	
	MainGrid.SetArgument("ssid", ssid);
	MainGrid.SetArgument("CName", CName);
	MainGrid.SetArgument("tName", tName);
	MainGrid.SetArgument("TsId", TsId);
	MainGrid.SetArgument("Addr", Addr);
	MainGrid.SetArgument("TaxNm", TaxNm);
	MainGrid.SetArgument("day", day);
	MainGrid.SetArgument("MAGAM", chkEND.Value);//마감여부

	getHAYZ082R_2();

	//MainGrid.Retrieve();

	http.Sync=false;
}
function getHAYZ082R_2(){

