/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getHMDZ160(){

	var sKind		= "getHMDZ160";						
	var sMethodName = "service::pshumlib/selectHMDZ160.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HMDZ160=ds_HMDZ160";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);			
		sArgument += "@SABUN=" + quote(ed_SABUN.Text);			
		sArgument += "@CNDCD=" + quote(chK_CNDCD.Value);	//퇴사자포함여부
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setHMDZ160(){

 	if(!validationChk()){
		return;
	}
/*
 	if(ds_HMDZ160.GetRowType(ds_HMDZ160.currow)=="update" ||ds_HMDZ160.GetRowType(ds_HMDZ160.currow)=="insert" ){
		if(!ds_HMDZ160_CanRowPosChange()){
			return;
		}
	} 
*/	
	var sKind		= "setHMDZ160";						
	var sMethodName = "service::pshumlib/saveHMDZ160.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_HMDZ160=ds_HMDZ160:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HMDZ160_CHECK=ds_HMDZ160_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " SABUN=" + quote(ed_SABUN.Text);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_HMDZ160_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_HMDZ160_CHECK.GetRowCount();i++){		
			alert("일련번호 : " +ds_HMDZ160_CHECK.GetColumn(i,"RELSQ") + ", 성명 : " +ds_HMDZ160_CHECK.GetColumn(i,"RELNM") + " = " + ds_FHDC600_CHECK.GetColumn(i,"RTMSG"));
		}
		getHMDZ160();        //error항목이 존재시에는 재조회		
		return;		
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	ed_SABUN.BKColor="white";

	if(length(trim(st_KORNM.Text)) == 0){
		ed_SABUN.BKColor="paleturquoise";
		alert("[알림]사번을 입력하세요.");	
		//alert(setPopMessage("6120"));//[알림]사번을 입력하세요.
		//setMessage("6120");
		ed_SABUN.SetFocus();
		result = false;
	}
	return result;
}

