function fn_Search()
{
	getHMDZ160();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_HMDZ160")== false) {
		return;
	}
	if(ds_HMDZ160.rowcount < 1) {
		fn_Search();
	}
	var p_RELSQ=0;
		p_RELSQ =parseInt(ds_HMDZ160.GetColumn(ds_HMDZ160.rowcount-1,"RELSQ"));
	ds_HMDZ160.AddRow();
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"SABUN",ed_SABUN.Text);
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"KORNM",st_KORNM.Text);
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"RELSQ",p_RELSQ+1);	//일련번호
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"RELCD",0);	//관계코드
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"BRDAT",0);	//생년월일
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"HAKCD",0);	//학력코드
	ds_HMDZ160.SetColumn(ds_HMDZ160.currow,"JOBCD",00);	//직업코드

	MainGrid.SetCellPos(1);
	MainGrid.SetFocus();
}

function fn_Delete()
{
	if(ds_HMDZ160.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_HMDZ160.DeleteRow(ds_HMDZ160.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

