/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getHPIF(){
	http.Sync = true;
	var sKind		= "getHPIF";						
	var sMethodName = "service::pshumlib/selectHPIF.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "DS_MAIN=DS_MAIN";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";


	if(length(trim(Calendar0.Value)) == 0){
		alert("조회월을 선택하세요!");	
		Calendar0.SetFocus();
		return;
	}

	sArgument += "@YYYYMM=" + quote(LEFT(Calendar0.Value,6));
	sArgument += "@SIGN=" + quote(Combo0.Value);

	dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument, 272, 64, "TitleBar=false");				
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");		
	}else {
		DS_MAIN.rowposition = -1;
	}	
}

