function setExcel()
{
	var fName   = '\\' + left(Calendar0.Value,6) + "_급여인터페이스";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|급여인터페이스";	
	createExcelSheetS2(FileDialog0, fName, arrSheets);
}

function UserList_OnFocus(obj)
{
