function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	//getMst040();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	cal_PLNYM.Value = nowDate;		
	cal_PLNYM.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	//MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBHZE212Q(){
	if(length(trim(cal_PLNYM.Value)) < 8){
		alert(setPopMessage("2528"));  //[[확인] 마감년월을 확인바랍니다.
		setMessage("2528");	
		cal_PLNYM.SetFocus();
		return;
	}
	var p_LOCA1,p_LOCA2,p_STGB,p_HOUE;
	p_LOCA1 = iif (length(trim(ed_LOCA1.Text)) == 0, ' ', trim(ed_LOCA1.Text));
	p_LOCA2 = iif (length(trim(ed_LOCA2.Text)) == 0 , '9999999', trim(ed_LOCA2.Text));

	if(!(ed_STGB.Value == "" || ed_STGB.Value == " " || ed_STGB.Value =="Y")){
		ed_STGB.Text = "Y";
	}
	if(length(trim(ed_STGB.Value)) < 1){
		p_STGB = " ";
	}else{
		p_STGB = ed_STGB.Text;
	}

	if(cb_searchCodeName1.Value = "E1"){
		p_HOUE="1";
	}else if(cb_searchCodeName1.Value = "C1"){
		p_HOUE="1";
	}else if(cb_searchCodeName1.Value = "S1"){
		p_HOUE="2";
	}else if(cb_searchCodeName1.Value = "H1"){
		p_HOUE="3";
	}

	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	if (length(trim(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"))) < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	//리모트콜 실행(BHZE212RC)
	param = cb_searchCodeName1.Value + substr(cal_PLNYM.Value,0,6) + 
	        left(p_LOCA1+"       ",7) + left(p_LOCA2+"       ",7) +
	        p_STGB+p_HOUE;
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	MainGrid.Visible = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		st_Msg.Visible = false;
		MainGrid.Visible = true;
		ReleaseCapture();
		SetWaitCursor(false);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var sKind		= "getBHZE212Q";						
				var sMethodName = "service::psimmlib/selectBHZE212Q.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_BHZE212Q=ds_BHZE212Q";    				// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += "@LIB1=" + quote(Lib1);
				sArgument += "@LIB2=" + quote(Lib2); 
				var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
				//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
				if (str == 'T'){  //정상조회
					alert(setPopMessage("1004"));
					setMessage("1004");	
				}else if (str == 'B'){   //data값이 없을때
					alert(setPopMessage("1003"));
					setMessage("1003");	
				}else if (str == 'E'){   //error발생시
					alert(setPopMessage("1002"));
					setMessage("1002");	
				}
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		st_Msg.Visible = true;
		MainGrid.Visible = false;
		SetWaitCursor(true);
		SetCapture();
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "외주부품 로케이션별 재고현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|외주부품 로케이션별 재고현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

