	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBZZE401Q(){
	if(length(trim(ed_SEQ.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		ed_SEQ.SetFocus();
		return;
	}
	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_CUVND1.Text)) == 0, ' ', trim(ed_CUVND1.Text));
	p_vnd2 = iif (length(trim(ed_CUVND2.Text)) == 0 , '999999', trim(ed_CUVND2.Text));

	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	if (length(trim(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"))) < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	//리모트콜 실행(BHZE215RC)
	param = cb_searchCodeName1.Value + right("000"+ed_SEQ.Value,3) + right("000000"+p_vnd1,6) + right("000000"+p_vnd2,6);
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	MainGrid.Visible = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		st_Msg.Visible = false;
		MainGrid.Visible = true;
		ReleaseCapture();
		SetWaitCursor(false);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//사내창고
				var sKind		= "getSC008PR";						
				var sMethodName = "service::pscomlib/selectSC008PR.do"; 
				var sInDataSet  = "";
				var sOutDataSet = "ds_SC008PR=ds_SC008PR";
				var sArgument   = "";
					sArgument   += " DTANO='HAC'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
					sArgument   += " LIB1=" + quote(Lib1);	
				http.Sync 	= true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync 	= false;
				var arrHouseHACT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
				var arrHouseHAC = "('";
				for (var i=0 ; toNumber(length(arrHouseHACT)) > i ; i++){
					if (i == length(arrHouseHACT)-1){
						arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "')";
					}else{
						arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "','";
					}
				}
				//사급창고
				var sKind		= "getSC008PR";						
				var sMethodName = "service::pscomlib/selectSC008PR.do"; 
				var sInDataSet  = "";
				var sOutDataSet = "ds_SC008PR=ds_SC008PR";
				var sArgument   = "";
					sArgument   += " DTANO='HAP'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
					sArgument   += " LIB1=" + quote(Lib1);	
				http.Sync 	= true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				http.Sync 	= false;
				var arrHouseHAPT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
				var arrHouseHAP = "('";
				for (var i=0 ; toNumber(length(arrHouseHAPT)) > i ; i++){
					if (i == length(arrHouseHAPT)-1){
						arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "')";
					}else{
						arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "','";
					}
				}

				var sKind		= "getBHZE215Q";						
				var sMethodName = "service::psimmlib/selectBHZE215Q.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_BHZE215Q=ds_BHZE215Q";    				// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += "@LIB1=" + quote(Lib1);
				sArgument += "@LIB2=" + quote(Lib2);
				sArgument += "@SEQ=" + quote(ed_SEQ.Value);
				sArgument += "@VNDNR1=" + quote(p_vnd1);
				sArgument += "@VNDNR2=" + quote(p_vnd2);
				sArgument += "@HOUSE_HAC=" + quote(arrHouseHAC);  //사내창고
				sArgument += "@HOUSE_HAP=" + quote(arrHouseHAP);  //사급창고
				sArgument += "@TRNYM=" + quote(substr(nowDate,0,6)); //현재년월
				var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
				//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
				var vndnr_1 = "";
				var vndnr_bef = "";
				var seq = 0;
				for(var i=0;i<ds_BHZE215Q.rowcount;i++){
					vndnr_1 = ds_BHZE215Q.GetColumn(i,"VNDNR");
					if (vndnr_1 == vndnr_bef){
						seq = seq+1;
					}else{
						seq = 1;
					}
					ds_BHZE215Q.SetColumn(i,"SEQ",seq);

					vndnr_bef = vndnr_1;
				}
				ds_BHZE215Q.Filter("toNumber(SEQ)<=" + toNumber(ed_SEQ.Text) + "" );
				//for(var i=0;i<ds_BHZE215Q.rowcount;i++){
				//	if(toNumber(ds_BHZE215Q.GetColumn(i,"SEQ")) > toNumber(ed_SEQ.Text)) ds_BHZE215Q.DeleteRow(ds_BHZE215Q.currow);
				//}
				MainGrid.BindDataset = "ds_BHZE215Q";
				if (str == 'T'){  //정상조회
					alert(setPopMessage("1004"));
					setMessage("1004");	
				}else if (str == 'B'){   //data값이 없을때
					alert(setPopMessage("1003"));
					setMessage("1003");	
				}else if (str == 'E'){   //error발생시
					alert(setPopMessage("1002"));
					setMessage("1002");	
				}
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		st_Msg.Visible = true;
		MainGrid.Visible = false;
		SetWaitCursor(true);
		SetCapture();
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "외주부품 소요량 산출금액 현황_협력업체별";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|외주부품 소요량 산출금액 현황_협력업체별";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

